package dev.sublab.keccak

import dev.sublab.hex.hex
import kotlin.test.Test
import kotlin.test.assertContentEquals

class Test {
    private val testCases = listOf(
        TestCase("1", "".hex.decode()),
        TestCase("2", "".hex.decode()),
        TestCase("3", "".hex.decode()),
        TestCase("4", "".hex.decode()),
        TestCase("5", "".hex.decode()),
        TestCase("6", "".hex.decode()),
        TestCase("7", "".hex.decode()),
        TestCase("8", "".hex.decode()),
        TestCase("9", "".hex.decode()),
        TestCase("0", "".hex.decode()),
        TestCase("00", "".hex.decode()),
        TestCase("12", "".hex.decode()),
        TestCase("94", "".hex.decode()),
    )

    @Test
    internal fun testF1600() {
        for (case in testCases) {
            val result = case.value.toByteArray().keccak.f1600()
            assertContentEquals(case.expectedHash, result)
        }
    }
}